sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue == '') {
      return;
   }

	var wutneeded = g_form.getValue('u_what_needed');

   //clear all choices
	g_form.clearOptions('u_what_needed');

	switch (newValue) {

		case 'hr': {
			g_form.addOption('u_what_needed', 'hr1', 'Hooman Resurrections 1');
			g_form.addOption('u_what_needed', 'hr2', 'Inhuman Resources 2');
			//
		} break;

		case 'facilities': {
			g_form.addOption('u_what_needed', 'facilitiitties1', 'facilitiitties 1');
			g_form.addOption('u_what_needed', 'facilitiitties2', 'facilitiitties 2');
			g_form.addOption('u_what_needed', 'other', 'other');
		}
			break;

		case 'legal':
			g_form.addOption('u_what_needed', 'other', 'other');
	}

	if (isLoading && !g_form.isNewRecord()) {
		g_form.setValue('u_what_needed', wutneeded);
	}


}
