sys_script_include.script:
var NeedItUtensils = Class.create();
NeedItUtensils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate) {
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnao = new GlideDateTime();
		var testOfTime = new GlideDateTime(strDate);

		// if testdate is b4 rightnao, return true, otherwise return home and cry to ur mother
		if (testOfTime.before(rightnao)) return true;
		else return false;
	},

	isDateToday: function(strDate) {
		var tomorrow = new GlideDateTime();
		tomorrow.addDaysLocalTime(+1);
		var testOfDays = new GlideDateTime(strDate);

		if (testOfDays.before(tomorrow)) {
			gs.addInfoMessage("tomorrow is " + tomorrow.getLocalDate());
			return true;
		}
		else return false;
	},

    type: 'NeedItUtensils'
};
